-- Drop table if it already exists to ensure a clean creation
DROP TABLE IF EXISTS public.products;

-- Create the products table
CREATE TABLE public.products (
    id text PRIMARY KEY,
    name text NOT NULL,
    category text NOT NULL,
    image text,
    rate text NOT NULL,
    unit text NOT NULL,
    "transhipmentRates" jsonb NOT NULL,
    "regionWiseRates" jsonb NOT NULL,
    policies text[] NOT NULL
);

-- Enable Row Level Security (RLS) - good practice for security
ALTER TABLE public.products ENABLE ROW LEVEL SECURITY;

-- Create a policy to allow anonymous users to read products
-- This is important for your public-facing product portal
CREATE POLICY "Enable read access for all users" ON public.products
FOR SELECT USING (true);

-- Insert initial product data

-- Policy to allow authenticated users to insert, update, and delete products
CREATE POLICY "Enable write access for authenticated users" ON public.products
FOR ALL TO authenticated USING (auth.uid() IS NOT NULL) WITH CHECK (auth.uid() IS NOT NULL);
